import { Component, OnInit  } from '@angular/core';

@Component({
    selector: 'app-login',
    templateUrl: './login.component.html',
    styleUrls: ['./login.component.css']
  })
export class LoginComponent implements OnInit {
  ngOnInit(): void {
      throw new Error('Method not implemented.');
  }

  public login(username: string, password: string, userType: string): boolean {
    // Aquí puedes implementar la lógica para verificar el inicio de sesión en el lado del cliente.
    // Por ejemplo, podrías comparar los datos con una lista de usuarios predefinidos.
    if (userType === 'estudiante' && username === 'estudiante' && password === 'estudiante123') {
      return true;
    } else if (userType === 'arrendatario' && username === 'arrendatario' && password === 'arrendatario123') {
      return true;
    } else {
      return false;
    }
  }
}
